mapper.select:
SELECT INDENTID AS id, INDENTNAME AS indentName, INDENTPRICE AS indentPrice, DATE_FORMAT(ORDERDATE,'%Y-%m-%d %T') AS orderDate, INDENTTYPE AS indentType, INDENT_TELE AS indent_tele, INDENT_RECOMMENT AS indent_recomment, INDENT_DESCRIPTION AS indent_description, SALESMANUNIQUEID AS salesmanUniqueId, sale.SALESMANNAME AS salesmanName FROM ind LEFT JOIN sale ON ind.salesmanUniqueId = sale.uniqueId
INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')
AND INDENTTYPE = ${indentType}
AND INDENT_TELE LIKE CONCAT(CONCAT('%',#{indent_tele}),'%')
AND DATE_FORMAT(orderDate,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(orderDate,'%Y-%m-%d') <= #{endTime}
ORDER BY
${sort} ${order},
ORDERDATE desc limit ${begin}, ${limit}

SELECT COUNT(1)
FROM



		INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')


		AND INDENTTYPE = ${indentType}


		AND INDENT_TELE LIKE
		CONCAT(CONCAT('%',#{indent_tele}),'%')


		AND DATE_FORMAT(orderDate,'%Y-%m-%d') >=
		#{beginTime}


		AND DATE_FORMAT(orderDate,'%Y-%m-%d') <=
		#{endTime}

SELECT INDENTID AS id, INDENTNAME AS indentName, INDENTPRICE AS indentPrice, DATE_FORMAT(ORDERDATE,'%Y-%m-%d %T') AS orderDate, INDENTTYPE AS indentType, INDENT_TELE AS indent_tele, INDENT_RECOMMENT AS indent_recomment, INDENT_DESCRIPTION AS indent_description, SALESMANUNIQUEID AS salesmanUniqueId FROM
INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')
AND INDENTTYPE = ${indentType}
AND INDENT_TELE LIKE CONCAT(CONCAT('%',#{indent_tele}),'%')
AND DATE_FORMAT(orderDate,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(orderDate,'%Y-%m-%d') <= #{endTime}
ORDER BY
${sort} ${order},
ORDERDATE desc

mapper.update:
UPDATE
INDENTNAME = #{indentName}, SECOND = #{second}, INDENTPRICE = ${indentPrice}, INDENTTYPE = ${indentType}, INDENT_TELE =#{indent_tele}, ORDERDATE = DATE_FORMAT(#{orderDate},'%Y-%m-%d %T'), INDENT_RECOMMENT = #{indent_recomment}, INDENT_DESCRIPTION = #{indent_description}, SALESMANUNIQUEID = #{salesmanUniqueId}
INDENTID = #{id}

UPDATE
INDENT_RECOMMENT = #{indent_recomment}, INDENTPRICE = ${indentPrice}, ORDERDATE = CURRENT_TIMESTAMP
INDENTID = #{id}
