mapper.select:
SELECT INDENTID AS id, INDENTNAME AS indentName, INDENTPRICE AS indentPrice, DATE_FORMAT(ORDERDATE, '%Y-%m-%d %T') AS orderDate, INDENTTYPE AS indentType, INDENT_TELE AS indent_tele, INDENT_RECOMMENT AS indent_recomment, INDENT_DESCRIPTION AS indent_description, SALESMANUNIQUEID AS salesmanUniqueId, sale.SALESMANNAME AS salesmanName, ind.EMPLOYEEID AS employeeId, ind.USERID AS userId, IFNULL(us.userCompany,ind.USERCOMPANY) AS userCompany, IFNULL(us.REALNAME,ind.REALNAME) AS realName, IFNULL(us.wechat,ind.WECHAT) AS wechat, IFNULL(us.position,ind.POSITION) AS position, ind.REQUIREID AS requireId, ind.CSRecomment AS cSRecomment, ind.PMRecomment AS pMRecomment, ind.indentSource AS indentSource, ind.referrerId AS referrerId, ind.pMId AS pMId FROM
ind LEFT JOIN
sale ON ind.salesmanUniqueId = sale.uniqueId LEFT JOIN USERS us ON ind.userId = us.id
INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')
AND INDENTTYPE = ${indentType}
AND INDENTTYPE IN
#{item}
AND INDENT_TELE LIKE CONCAT(CONCAT('%',#{indent_tele}),'%')
AND DATE_FORMAT(orderDate,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(orderDate,'%Y-%m-%d') <= #{endTime}
AND EMPLOYEEID = #{employeeId}
AND INDENTSOURCE = #{indentSource}
AND USERCOMPANY LIKE CONCAT(CONCAT('%',#{userCompany}),'%')
AND REALNAME LIKE CONCAT(CONCAT('%',#{realName}),'%')
ORDER BY
${sort} ${order},
ORDERDATE desc limit ${begin}, ${limit}

SELECT COUNT(1)
FROM



		INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')


		AND INDENTTYPE = ${indentType}


		AND INDENTTYPE IN

				#{item}



		AND INDENT_TELE LIKE
		CONCAT(CONCAT('%',#{indent_tele}),'%')


		AND DATE_FORMAT(orderDate,'%Y-%m-%d') >=
		#{beginTime}


		AND DATE_FORMAT(orderDate,'%Y-%m-%d') <=
		#{endTime}


		AND EMPLOYEEID = #{employeeId}


		AND INDENTSOURCE = #{indentSource}


		AND USERCOMPANY LIKE
		CONCAT(CONCAT('%',#{userCompany}),'%')


		AND REALNAME LIKE
		CONCAT(CONCAT('%',#{realName}),'%')

SELECT INDENTID AS id, INDENTNAME AS indentName, INDENTPRICE AS indentPrice, DATE_FORMAT(ORDERDATE,'%Y-%m-%d %T') AS orderDate, INDENTTYPE AS indentType, INDENT_TELE AS indent_tele, INDENT_RECOMMENT AS indent_recomment, INDENT_DESCRIPTION AS indent_description, SALESMANUNIQUEID AS salesmanUniqueId FROM
INDENTID LIKE CONCAT(CONCAT('%',#{id}),'%')
AND INDENTTYPE = ${indentType}
AND INDENTTYPE IN
#{item}
AND INDENT_TELE LIKE CONCAT(CONCAT('%',#{indent_tele}),'%')
AND DATE_FORMAT(orderDate,'%Y-%m-%d') >= #{beginTime}
AND DATE_FORMAT(orderDate,'%Y-%m-%d') <= #{endTime}
AND EMPLOYEEID = #{employeeId}
AND INDENTSOURCE = #{indentSource}
AND USERCOMPANY LIKE CONCAT(CONCAT('%',#{userCompany}),'%')
AND REALNAME LIKE CONCAT(CONCAT('%',#{realName}),'%')
ORDER BY
${sort} ${order},
ORDERDATE desc

mapper.update:
UPDATE
INDENT_RECOMMENT = #{indent_recomment}, INDENTPRICE = ${indentPrice}, ORDERDATE = CURRENT_TIMESTAMP
INDENTID = #{id}

